mapper.select:
SELECT sn.* ,lgroups.title as title ,lgroups.content as content ,lgroups.likeid as likeid ,lgroups.topnum as topnum ,lgroups.pic as pic ,lemp.nickname as nickname ,lemp.cover as cover from emp_groups sn LEFT JOIN happy_hand_group lgroups ON lgroups.groupid = sn.groupid LEFT JOIN emp lemp ON lemp.empid = sn.empid WHERE 1=1
AND sn.empid = #{empid}
AND sn.groupid = #{groupid}

SELECT sn.* ,lgroups.title as title,lgroups.content as content,lgroups.likeid as likeid,lgroups.topnum as topnum,lgroups.pic as pic
from emp_groups sn
LEFT  JOIN  happy_hand_group lgroups ON lgroups.groupid = sn.groupid
WHERE 1=1

    AND sn.empid = #{empid}


    AND sn.groupid = #{groupid}
