mapper.select:
SELECT sn.*, lemp1.cover as empid1Cover, lemp1.nickname as empid1Nickname, lemp2.cover as empid2Cover, lemp2.nickname as empid2Nickname FROM happy_hand_jw sn LEFT JOIN emp lemp1 ON lemp1.empid = sn.empid1 LEFT JOIN emp lemp2 ON lemp2.empid = sn.empid2 where 1 = 1
AND sn.empid1 = #{empid1}
AND sn.empid2 = #{empid2}
AND sn.is_check = #{is_check}

SELECT count(sn.jwid) FROM happy_hand_jw sn
WHERE 1=1

    AND sn.empid1 = #{empid1}


    AND sn.empid2 = #{empid2}


    AND sn.is_check = #{is_check}
